import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import './ListingsPage.css';
import { FaSearch, FaGlobe, FaUserCircle, FaBars } from 'react-icons/fa';

function ListingsPage() {
  const { location } = useParams();
  const [listings, setListings] = useState([]);
  const [locations, setLocations] = useState([]);
  const [showLocationDropdown, setShowLocationDropdown] = useState(false);
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [username, setUsername] = useState('');
  const [profileDropdownOpen, setProfileDropdownOpen] = useState(false);
  const navigate = useNavigate();
  const [loading, setLoading] = useState(true);

  // Fetch listings for the selected location
  useEffect(() => {
    const fetchListings = async () => {
      try {
        const locationParam = location === 'all' ? 'all' : location;
        const response = await fetch(`http://localhost:5000/api/listings/${locationParam}`);
        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }
        const data = await response.json();
        setListings(data);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching listings:', error.message);
        setLoading(false);
      }
    };
    fetchListings();
  }, [location]);

  // Fetch all locations
  useEffect(() => {
    const fetchLocations = async () => {
      try {
        const response = await fetch('http://localhost:5000/api/locations');
        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }
        const data = await response.json();
        setLocations(['All locations', ...data]);
      } catch (error) {
        console.error('Error fetching locations:', error.message);
      }
    };
    fetchLocations();
  }, []);

  // Handle location selection
  const handleLocationSelect = (selectedLocation) => {
    if (selectedLocation === 'All locations') {
      navigate('/listings/all');
    } else {
      navigate(`/listings/${selectedLocation}`);
    }
    setShowLocationDropdown(false);
  };

  // Check for logged-in user
  useEffect(() => {
    const storedUsername = localStorage.getItem('username');
    if (storedUsername) {
      setIsLoggedIn(true);
      setUsername(storedUsername);
    }
  }, []);

  const handleLogout = () => {
    localStorage.removeItem('username');
    localStorage.removeItem('role');
    setIsLoggedIn(false);
    setUsername('');
    navigate('/');
  };

  const toggleProfileDropdown = () => {
    setProfileDropdownOpen((prev) => !prev);
  };

  const toggleLocationDropdown = () => {
    setShowLocationDropdown((prev) => !prev);
  };

  return (
    <div className="listings-page">
      {/* Navbar */}
      <header className="navbar">
        <div className="navbar-logo" onClick={() => navigate('/')}>
          <img
            src="https://logos-world.net/wp-content/uploads/2020/10/Airbnb-Logo-2014.png"
            alt="Airbnb Logo"
          />
        </div>
        <div className="navbar-center">
          <span>Places to stay</span>
          <span>Experiences</span>
          <span>Online Experiences</span>
        </div>
        <div className="navbar-end">
          <span className="navbar-host" onClick={() => navigate('/host-login')}>
            Become a Host
          </span>
          <FaGlobe className="icon globe-icon" />
          <div className="profile-section" onClick={toggleProfileDropdown}>
            <FaBars className="menu-icon" />
            <FaUserCircle className="user-icon" />
            {profileDropdownOpen && (
              <div className="profile-dropdown-menu">
                {isLoggedIn ? (
                  <span onClick={handleLogout}>Logout</span>
                ) : (
                  <span onClick={() => navigate('/user-login')}>Login</span>
                )}
              </div>
            )}
          </div>
          {isLoggedIn && <span className="welcome-text">Welcome, {username}</span>}
        </div>
      </header>

      {/* Search Bar */}
      <section className="search-section">
        <div className="search-bar">
          <div className="search-field">
            <label>Location</label>
            <span onClick={toggleLocationDropdown} className="dropdown-link">
              Select Location
            </span>
            {showLocationDropdown && (
              <div className="location-dropdown-content">
                {locations.map((loc, index) => (
                  <span key={index} onClick={() => handleLocationSelect(loc)}>
                    {loc}
                  </span>
                ))}
              </div>
            )}
          </div>
          <div className="search-field">
            <label>Check in</label>
            <span className="dropdown-link">Add dates</span>
          </div>
          <div className="search-field">
            <label>Check out</label>
            <span className="dropdown-link">Add dates</span>
          </div>
          <div className="search-field">
            <label>Guests</label>
            <input type="text" placeholder="Add guests" />
          </div>
          <button className="search-button">
            <FaSearch />
          </button>
        </div>
      </section>

      {/* Listings */}
      <section className="listings-container">
        {loading ? (
          <p>Loading listings...</p>
        ) : listings.length > 0 ? (
          listings.map((listing) => (
            <div key={listing._id} className="listing-card">
              <div className="listing-left">
                <img
                  src={listing.imageUrl || '/placeholder.jpg'}
                  alt={listing.title}
                  className="listing-image"
                />
              </div>
              <div className="listing-details">
                <h3>{listing.location}</h3>
                <h2>{listing.title}</h2>
                <div className="listing-info">
                  <span>{listing.guests} Guests</span>
                  <span>{listing.rooms} Rooms</span>
                  <span>{listing.baths} Baths</span>
                </div>
                <div className="listing-amenities">
                  <h3>Amenities:</h3>
                  <ul>
                    {listing.amenities.map((amenity, index) => (
                      <li key={index}>{amenity}</li>
                    ))}
                  </ul>
                </div>
                <div className="listing-footer">
                  <span className="listing-price">${listing.price} / night</span>
                </div>
              </div>
            </div>
          ))
        ) : (
          <p>No listings found for this location.</p>
        )}
      </section>
    </div>
  );
}

export default ListingsPage;
