import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams, useNavigate } from 'react-router-dom';

const UpdateListing = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [form, setForm] = useState({
    title: '',
    location: '',
    description: '',
    rooms: '',
    beds: '',
    guests: '',
    baths: '',
    price: '',
    amenities: [],
  });

  useEffect(() => {
    const fetchListing = async () => {
      try {
        const token = localStorage.getItem('token');
        const response = await axios.get(`http://localhost:5000/api/accommodations/${id}`, {
          headers: { Authorization: `Bearer ${token}` },
        });
        const listing = response.data;
        setForm({
          title: listing.title,
          location: listing.location,
          description: listing.description,
          rooms: listing.rooms,
          beds: listing.beds,
          guests: listing.guests,
          baths: listing.baths,
          price: listing.price,
          amenities: listing.amenities || [],
        });
      } catch (error) {
        console.error('Error fetching listing:', error.message);
      }
    };
    fetchListing();
  }, [id]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem('token');
      const response = await axios.put(
        `http://localhost:5000/api/accommodations/${id}`,
        {
          ...formData,
          amenities: formData.amenities.split(',').map((a) => a.trim()), // Convert back to an array
        },
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );
      const updatedListing = response.data; // Get updated listing
      navigate('/view-listings', { state: { updatedListing } }); // Pass updated listing to View Listings
    } catch (error) {
      console.error('Error updating listing:', error.response?.data || error.message);
    }
  };
  

  return (
    <form className="listing-form" onSubmit={handleSubmit}>
      <h2>Update Listing</h2>
      <input
        type="text"
        name="title"
        value={formData.title}
        onChange={handleChange}
        placeholder="Title"
      />
      <input
        type="text"
        name="area"
        value={formData.area}
        onChange={handleChange}
        placeholder="Area"
      />
      <input
        type="number"
        name="guests"
        value={formData.guests}
        onChange={handleChange}
        placeholder="Number of Guests"
      />
      <input
        type="number"
        name="beds"
        value={formData.beds}
        onChange={handleChange}
        placeholder="Number of Beds"
      />
      <input
        type="number"
        name="baths"
        value={formData.baths}
        onChange={handleChange}
        placeholder="Number of Baths"
      />
      <textarea
        name="amenities"
        value={formData.amenities}
        onChange={handleChange}
        placeholder="Amenities (comma-separated)"
      />
      <input
        type="number"
        name="rating"
        value={formData.rating}
        onChange={handleChange}
        placeholder="Rating"
      />
      <input
        type="number"
        name="pricePerNight"
        value={formData.pricePerNight}
        onChange={handleChange}
        placeholder="Price per Night"
      />
      <input
        type="text"
        name="image"
        value={formData.image}
        onChange={handleChange}
        placeholder="Image URL"
      />
      <button type="submit">Save</button>
    </form>
  );
};

export default UpdateListing;
