import React, { useEffect, useState } from "react";
import {
  getAccommodations,
  createAccommodation,
  deleteAccommodation,
  updateAccommodation,
} from "../services/accommodationService";
import { getReservationsByHost, deleteReservation } from "../services/reservationService";

const Dashboard = () => {
  const [accommodations, setAccommodations] = useState([]);
  const [newAccommodation, setNewAccommodation] = useState({
    title: "",
    location: "",
    description: "",
    price: 0,
    guests: 1,
    bedrooms: 1,
    bathrooms: 1,
    amenities: [],
    images: [],
  });
  const [isEditing, setIsEditing] = useState(false);
  const [selectedAccommodation, setSelectedAccommodation] = useState(null);

  useEffect(() => {
    // Fetch accommodations when the component mounts
    const fetchData = async () => {
      const data = await getAccommodations();
      setAccommodations(data);
    };
    fetchData();
  }, []);

  const [reservations, setReservations] = useState([]);

  // Fetch reservations
  useEffect(() => {
    const fetchReservations = async () => {
      const data = await getReservationsByHost();
      setReservations(data);
    };
    fetchReservations();
  }, []);

  const handleCreate = async (e) => {
    e.preventDefault();

    try {
      const createdAccommodation = await createAccommodation(newAccommodation);
      setAccommodations([...accommodations, createdAccommodation]);
      setNewAccommodation({
        title: "",
        location: "",
        description: "",
        price: 0,
        guests: 1,
        bedrooms: 1,
        bathrooms: 1,
        amenities: [],
        images: [],
      });
    } catch (error) {
      console.error("Error creating accommodation:", error);
      alert("Failed to create accommodation");
    }
  };

  const handleDelete = async (id) => {
    try {
      await deleteAccommodation(id);

      // Update the accommodations list by filtering out the deleted item
      setAccommodations(accommodations.filter((acc) => acc._id !== id));
    } catch (error) {
      console.error("Error deleting accommodation:", error);
      alert("Failed to delete accommodation");
    }
  };

  const handleEdit = (accommodation) => {
    setSelectedAccommodation(accommodation);
    setIsEditing(true);
  };

  const handleUpdate = async (e) => {
    e.preventDefault();
    try {
      const updatedAccommodation = await updateAccommodation(
        selectedAccommodation._id,
        selectedAccommodation
      );
      setAccommodations(
        accommodations.map((acc) =>
          acc._id === updatedAccommodation._id ? updatedAccommodation : acc
        )
      );
      setIsEditing(false);
      setSelectedAccommodation(null);
    } catch (error) {
      console.error("Error updating accommodation:", error);
      alert("Failed to update accommodation");
    }
  };

  

  return (
    <div>
      <h2>Dashboard</h2>

      {isEditing ? (
        <div>
          <h3>Edit Accommodation</h3>
          <form onSubmit={handleUpdate}>
            <input
              type="text"
              placeholder="Title"
              value={selectedAccommodation.title}
              onChange={(e) =>
                setSelectedAccommodation({
                  ...selectedAccommodation,
                  title: e.target.value,
                })
              }
            />
            <input
              type="text"
              placeholder="Location"
              value={selectedAccommodation.location}
              onChange={(e) =>
                setSelectedAccommodation({
                  ...selectedAccommodation,
                  location: e.target.value,
                })
              }
            />
            <input
              type="text"
              placeholder="Description"
              value={selectedAccommodation.description}
              onChange={(e) =>
                setSelectedAccommodation({
                  ...selectedAccommodation,
                  description: e.target.value,
                })
              }
            />
            <input
              type="number"
              placeholder="Price"
              value={selectedAccommodation.price}
              onChange={(e) =>
                setSelectedAccommodation({
                  ...selectedAccommodation,
                  price: Number(e.target.value),
                })
              }
            />
            <input
              type="number"
              placeholder="Guests"
              value={selectedAccommodation.guests}
              onChange={(e) =>
                setSelectedAccommodation({
                  ...selectedAccommodation,
                  guests: Number(e.target.value),
                })
              }
            />
            <input
              type="number"
              placeholder="Bedrooms"
              value={selectedAccommodation.bedrooms}
              onChange={(e) =>
                setSelectedAccommodation({
                  ...selectedAccommodation,
                  bedrooms: Number(e.target.value),
                })
              }
            />
            <input
              type="number"
              placeholder="Bathrooms"
              value={selectedAccommodation.bathrooms}
              onChange={(e) =>
                setSelectedAccommodation({
                  ...selectedAccommodation,
                  bathrooms: Number(e.target.value),
                })
              }
            />
            <button type="submit">Save Changes</button>
            <button type="button" onClick={() => setIsEditing(false)}>
              Cancel
            </button>
          </form>
        </div>
      ) : (
        <div>
          <h3>Create New Accommodation</h3>
          <form onSubmit={handleCreate}>
            <input
              type="text"
              placeholder="Title"
              value={newAccommodation.title}
              onChange={(e) =>
                setNewAccommodation({
                  ...newAccommodation,
                  title: e.target.value,
                })
              }
            />
            <input
              type="text"
              placeholder="Location"
              value={newAccommodation.location}
              onChange={(e) =>
                setNewAccommodation({
                  ...newAccommodation,
                  location: e.target.value,
                })
              }
            />
            <input
              type="text"
              placeholder="Description"
              value={newAccommodation.description}
              onChange={(e) =>
                setNewAccommodation({
                  ...newAccommodation,
                  description: e.target.value,
                })
              }
            />
            <input
              type="number"
              placeholder="Price"
              value={newAccommodation.price}
              onChange={(e) =>
                setNewAccommodation({
                  ...newAccommodation,
                  price: Number(e.target.value),
                })
              }
            />
            <input
              type="number"
              placeholder="Guests"
              value={newAccommodation.guests}
              onChange={(e) =>
                setNewAccommodation({
                  ...newAccommodation,
                  guests: Number(e.target.value),
                })
              }
            />
            <input
              type="number"
              placeholder="Bedrooms"
              value={newAccommodation.bedrooms}
              onChange={(e) =>
                setNewAccommodation({
                  ...newAccommodation,
                  bedrooms: Number(e.target.value),
                })
              }
            />
            <input
              type="number"
              placeholder="Bathrooms"
              value={newAccommodation.bathrooms}
              onChange={(e) =>
                setNewAccommodation({
                  ...newAccommodation,
                  bathrooms: Number(e.target.value),
                })
              }
            />
            <button type="submit">Create</button>
          </form>
        </div>
      )}

      <h3>Existing Accommodations</h3>
      <ul>
        {accommodations.map((acc) => (
          <li key={acc._id}>
            {acc.title} - {acc.location} - ${acc.price}
            <button onClick={() => handleEdit(acc)}>Edit</button>
            <button onClick={() => handleDelete(acc._id)}>Delete</button>
          </li>
        ))}
      </ul>

      <h3>Reservations for My Accommodations</h3>
      <ul>
        {reservations.map((reservation) => (
          <li key={reservation._id}>
            {reservation.user.username} booked {reservation.accommodation.title}{" "}
            from {reservation.checkInDate} to {reservation.checkOutDate}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Dashboard;
