import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import '../components/ListingDetail';
import { FaStar } from 'react-icons/fa';

function ListingDetailsPage() {
  const { id } = useParams(); // Extract listing ID from URL
  const [listing, setListing] = useState(null);

  useEffect(() => {
    const fetchListingDetails = async () => {
      try {
        const response = await fetch(`http://localhost:5000/api/listing/${id}`);
        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }
        const data = await response.json();
        setListing(data);
      } catch (error) {
        console.error('Error fetching listing details:', error.message);
      }
    };

    fetchListingDetails();
  }, [id]);

  if (!listing) {
    return <p>Loading...</p>;
  }

  return (
    <div className="listing-details-page">
      {/* Navbar */}
      <header className="navbar">
        <div className="navbar-logo">
          <img
            src="https://logos-world.net/wp-content/uploads/2020/10/Airbnb-Logo-2014.png"
            alt="Airbnb Logo"
          />
        </div>
        <div className="navbar-center">
          <span>Places to stay</span>
          <span>Experiences</span>
          <span>Online Experiences</span>
        </div>
      </header>

      {/* Listing Details */}
      <section className="details-section">
        <div className="image-gallery">
          {listing.images.map((image, index) => (
            <img
              key={index}
              src={`http://localhost:5000/${image}`}
              alt={`Gallery ${index + 1}`}
              className="gallery-image"
            />
          ))}
        </div>

        <div className="listing-content">
          <div className="listing-info">
            <h1>{listing.title}</h1>
            <p>
              <FaStar className="star-icon" /> 5.0 • {listing.location}
            </p>
            <p>{listing.description}</p>
          </div>

          <div className="listing-amenities">
            <h2>Amenities</h2>
            <ul>
              {listing.amenities.map((amenity, index) => (
                <li key={index}>{amenity}</li>
              ))}
            </ul>
          </div>
        </div>

        <div className="reservation-card">
          <h2>${listing.price} / night</h2>
          <form>
            <label>Check-in</label>
            <input type="date" required />

            <label>Check-out</label>
            <input type="date" required />

            <label>Guests</label>
            <input type="number" min="1" required placeholder="Number of guests" />

            <button type="submit">Reserve</button>
          </form>
        </div>
      </section>
    </div>
  );
}

export default ListingDetailsPage;
