import React, { useState } from 'react';
import axios from 'axios';
import '../components/ViewListings.css';

const ViewListings = ({ listings, setListings }) => {
  const [isEditing, setIsEditing] = useState(null); // Track listing being edited
  const [editForm, setEditForm] = useState({
    title: '',
    location: '',
    description: '',
    rooms: '',
    baths: '',
    amenities: [],
    price: '',
  });

  const handleEditClick = (listing) => {
    setIsEditing(listing._id); // Set the ID of the listing being edited
    setEditForm({
      title: listing.title,
      location: listing.location,
      description: listing.description,
      rooms: listing.rooms,
      baths: listing.baths,
      amenities: listing.amenities,
      price: listing.price,
    });
  };

  const handleEditChange = (e) => {
    const { name, value } = e.target;
    setEditForm((prev) => ({ ...prev, [name]: value }));
  };

  const handleAddAmenity = () => {
    setEditForm((prev) => ({
      ...prev,
      amenities: [...prev.amenities, ''],
    }));
  };

  const handleAmenityChange = (index, value) => {
    const updatedAmenities = [...editForm.amenities];
    updatedAmenities[index] = value;
    setEditForm((prev) => ({ ...prev, amenities: updatedAmenities }));
  };

  const handleUpdate = async (listingId) => {
    try {
      const response = await axios.put(`/api/accommodations/${listingId}`, editForm);
      const updatedListing = response.data;

      // Update the local state
      setListings((prevListings) =>
        prevListings.map((listing) =>
          listing._id === listingId ? updatedListing : listing
        )
      );

      // Clear edit form and close edit mode
      setIsEditing(null);
      setEditForm({
        title: '',
        location: '',
        description: '',
        rooms: '',
        baths: '',
        amenities: [],
        price: '',
      });
    } catch (error) {
      console.error('Failed to update listing:', error);
    }
  };

  const handleDelete = async (listingId) => {
    try {
      await axios.delete(`/api/accommodations/${listingId}`);
      setListings((prevListings) => prevListings.filter((listing) => listing._id !== listingId));
    } catch (error) {
      console.error('Failed to delete listing:', error);
    }
  };

  return (
    <div className="listing-view">
      <h3>My Hotel List</h3>
      {listings.map((listing) => (
        <div key={listing._id} className="listing-item">
          <img src={listing.mainImage} alt="Hotel" />
          <div>
            <h4>{listing.title}</h4>
            <p>{listing.rooms} rooms • {listing.baths} baths</p>
            <p>
              {listing.amenities.join(' • ')}
              <span> • 5.0 ⭐</span> {/* Star rating placeholder */}
            </p>
            <div className="price">${listing.price} <span>/night</span></div>
            {isEditing === listing._id ? (
              <div className="edit-form">
                <input
                  type="text"
                  name="title"
                  value={editForm.title}
                  onChange={handleEditChange}
                  placeholder="Title"
                />
                <input
                  type="text"
                  name="location"
                  value={editForm.location}
                  onChange={handleEditChange}
                  placeholder="Location"
                />
                <textarea
                  name="description"
                  value={editForm.description}
                  onChange={handleEditChange}
                  placeholder="Description"
                />
                <input
                  type="number"
                  name="rooms"
                  value={editForm.rooms}
                  onChange={handleEditChange}
                  placeholder="Rooms"
                />
                <input
                  type="number"
                  name="baths"
                  value={editForm.baths}
                  onChange={handleEditChange}
                  placeholder="Baths"
                />
                <input
                  type="number"
                  name="price"
                  value={editForm.price}
                  onChange={handleEditChange}
                  placeholder="Price per Night"
                />

                <div className="amenities">
                  <label>Amenities</label>
                  {editForm.amenities.map((amenity, index) => (
                    <input
                      key={index}
                      type="text"
                      value={amenity}
                      onChange={(e) => handleAmenityChange(index, e.target.value)}
                      placeholder={`Amenity ${index + 1}`}
                    />
                  ))}
                  <button type="button" onClick={handleAddAmenity}>Add Amenity</button>
                </div>

                <button onClick={() => handleUpdate(listing._id)} className="save-btn">
                  Save
                </button>
                <button onClick={() => setIsEditing(null)} className="cancel-btn">
                  Cancel
                </button>
              </div>
            ) : (
              <>
                <button onClick={() => handleEditClick(listing)} className="update-btn">
                  Edit
                </button>
                <button onClick={() => handleDelete(listing._id)} className="delete-btn">
                  Delete
                </button>
              </>
            )}
          </div>
        </div>
      ))}
    </div>
  );
};

export default ViewListings;
