// frontend/src/pages/HomePage.js

import React, { useEffect, useState } from 'react';
import Navbar from '../components/Navbar';
import { getAccommodations } from '../services/accommodationService';
import { useNavigate } from 'react-router-dom';

const HomePage = () => {
    const [accommodations, setAccommodations] = useState([]);
    const [isLoggedIn, setIsLoggedIn] = useState(false);
    const [username, setUsername] = useState('');
    const navigate = useNavigate();

    // Check if user is logged in and get username
    useEffect(() => {
        const token = localStorage.getItem('token');
        const savedUsername = localStorage.getItem('username');
        if (token && savedUsername) {
            setIsLoggedIn(true);
            setUsername(savedUsername);
        }
    }, []);

    // Fetch accommodations on component mount
    useEffect(() => {
        const fetchAccommodations = async () => {
            const data = await getAccommodations();
            setAccommodations(data);
        };
        fetchAccommodations();
    }, []);

    // Logout function
    const handleLogout = () => {
        localStorage.removeItem('token');
        localStorage.removeItem('username');
        localStorage.removeItem('role');
        setIsLoggedIn(false);
        setUsername('');
        navigate('/');
    };

    return (
        <div>
            <Navbar isLoggedIn={isLoggedIn} username={username} onLogout={handleLogout} />

            <div style={styles.accommodationsContainer}>
                <h2>Available Accommodations</h2>
                <ul style={styles.accommodationsList}>
                    {accommodations.map((acc) => (
                        <li key={acc._id} style={styles.accommodationItem}>
                            <h3>{acc.title}</h3>
                            <p>Location: {acc.location}</p>
                            <p>Price: ${acc.price} per night</p>
                            <p>Guests: {acc.guests}</p>
                        </li>
                    ))}
                </ul>
            </div>
        </div>
    );
};

const styles = {
    accommodationsContainer: {
        padding: '2rem',
        textAlign: 'center',
    },
    accommodationsList: {
        listStyleType: 'none',
        padding: 0,
    },
    accommodationItem: {
        border: '1px solid #ddd',
        padding: '1rem',
        margin: '1rem 0',
        borderRadius: '8px',
        boxShadow: '0 2px 8px rgba(0, 0, 0, 0.1)',
    },
};

export default HomePage;
