import React, { useEffect, useState } from "react";
import axios from "axios";
import "../components/ViewReservations.css";

const ViewReservations = () => {
  const [reservations, setReservations] = useState([]);

  useEffect(() => {
    const fetchReservations = async () => {
      try {
        const response = await axios.get("http://localhost:5000/api/reservations/host", {
          headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
        });
        console.log("Host reservations data:", response.data); // Debugging line
        setReservations(response.data);
      } catch (error) {
        console.error("Error fetching reservations:", error.message);
      }
    };
    fetchReservations();
  }, []);

  const handleDelete = async (reservationId) => {
    try {
      await axios.delete(`http://localhost:5000/api/reservations/${reservationId}`, {
        headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
      });
      setReservations((prevReservations) =>
        prevReservations.filter((res) => res._id !== reservationId)
      );
    } catch (error) {
      console.error("Error deleting reservation:", error.message);
    }
  };

  return (
    <div className="reservations-container">
      <h1>Host Reservations</h1>
      <table className="reservations-table">
        <thead>
          <tr>
            <th>Booked By</th>
            <th>Property</th>
            <th>Check-in</th>
            <th>Check-out</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {reservations.map((res) => (
            <tr key={res._id}>
              <td>{res.user ? res.user.username : "Unknown User"}</td>
              <td>{`${res.accommodation.title}, ${res.accommodation.location}`}</td>
              <td>{new Date(res.checkIn).toLocaleDateString()}</td>
              <td>{new Date(res.checkOut).toLocaleDateString()}</td>
              <td>
                <button onClick={() => handleDelete(res._id)} className="delete-button">
                  Delete
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default ViewReservations;
