import React, { useState } from 'react';

const ListingForm = ({ onSubmit, initialData }) => {
    const [formData, setFormData] = useState(initialData || {
        title: '',
        location: '',
        description: '',
        bedrooms: 1,
        bathrooms: 1,
        guests: 1,
        type: '',
        price: 0,
        amenities: '',
        images: [],
        weeklyDiscount: 0,
        cleaningFee: 0,
        serviceFee: 0,
        occupancyTaxes: 0,
    });

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({ ...formData, [name]: value });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        onSubmit(formData);
    };

    return (
        <form onSubmit={handleSubmit}>
            <input type="text" name="title" value={formData.title} onChange={handleChange} placeholder="Title" required />
            <input type="text" name="location" value={formData.location} onChange={handleChange} placeholder="Location" required />
            <textarea name="description" value={formData.description} onChange={handleChange} placeholder="Description" required></textarea>
            <input type="number" name="bedrooms" value={formData.bedrooms} onChange={handleChange} placeholder="Bedrooms" min="1" required />
            <input type="number" name="bathrooms" value={formData.bathrooms} onChange={handleChange} placeholder="Bathrooms" min="1" required />
            <input type="number" name="guests" value={formData.guests} onChange={handleChange} placeholder="Guests" min="1" required />
            <input type="text" name="type" value={formData.type} onChange={handleChange} placeholder="Type" required />
            <input type="number" name="price" value={formData.price} onChange={handleChange} placeholder="Price" required />
            <input type="text" name="amenities" value={formData.amenities} onChange={handleChange} placeholder="Amenities" />
            <input type="number" name="weeklyDiscount" value={formData.weeklyDiscount} onChange={handleChange} placeholder="Weekly Discount" />
            <input type="number" name="cleaningFee" value={formData.cleaningFee} onChange={handleChange} placeholder="Cleaning Fee" />
            <input type="number" name="serviceFee" value={formData.serviceFee} onChange={handleChange} placeholder="Service Fee" />
            <input type="number" name="occupancyTaxes" value={formData.occupancyTaxes} onChange={handleChange} placeholder="Occupancy Taxes" />
            <button type="submit">Submit</button>
        </form>
    );
};

export default ListingForm;
