import React, { useEffect, useState } from 'react';
import axios from 'axios';
import '../components/ViewListings.css'

const ViewListings = () => {
  const [listings, setListings] = useState([]);

  useEffect(() => {
    const fetchListings = async () => {
      const response = await axios.get('/api/accommodations'); // Adjust route if necessary
      setListings(response.data);
    };
    fetchListings();
  }, []);

  const handleUpdate = (listingId) => {
    // Implement update functionality here
  };

  const handleDelete = async (listingId) => {
    await axios.delete(`/api/accommodations/${listingId}`);
    setListings(listings.filter(listing => listing._id !== listingId));
  };

  return (
    <div>
      <h2>My Listings</h2>
      {listings.map(listing => (
        <div key={listing._id} className="listing-card">
          <img src={listing.mainImage} alt="Hotel" />
          <div>
            <h3>{listing.title}</h3>
            <p>{listing.description}</p>
            <button onClick={() => handleUpdate(listing._id)} className="update-button">Update</button>
            <button onClick={() => handleDelete(listing._id)} className="delete-button">Delete</button>
          </div>
        </div>
      ))}
    </div>
  );
};

export default ViewListings;
