// frontend/src/components/Navbar.js

import React from 'react';
import { Link, useNavigate } from 'react-router-dom';

const Navbar = ({ isLoggedIn, username, onLogout }) => {
    const navigate = useNavigate();

    const handleLogout = () => {
        onLogout();
        navigate('/');  // Redirect to the home page after logout
    };

    return (
        <nav style={styles.navbar}>
            <h1 style={styles.logo}>Airbnb</h1>

            <input
                type="text"
                placeholder="Search"
                style={styles.searchBar}
            />

            <div style={styles.links}>
                {!isLoggedIn ? (
                    <>
                        <Link to="/host-login" style={styles.link}>Become a host</Link>
                        <Link to="/user-login" style={styles.link}>User Login</Link>
                    </>
                ) : (
                    <>
                        <span>Welcome, {username}</span>
                        <button onClick={handleLogout} style={styles.link}>Logout</button>
                    </>
                )}
            </div>
        </nav>
    );
};

const styles = {
    navbar: {
        display: 'flex',
        justifyContent: 'space-between',
        alignItems: 'center',
        padding: '1rem 2rem',
        backgroundColor: '#FF5A5F',
        color: '#fff',
    },
    logo: {
        fontSize: '1.5rem',
        fontWeight: 'bold',
    },
    searchBar: {
        padding: '0.5rem',
        width: '200px',
    },
    links: {
        display: 'flex',
        alignItems: 'center',
        gap: '1rem',
    },
    link: {
        color: '#fff',
        textDecoration: 'none',
        cursor: 'pointer',
        border: 'none',
        background: 'none',
    },
};

export default Navbar;
