import React, { useEffect, useState } from 'react';
import { getAccommodations, createAccommodation, deleteAccommodation } from '../services/accommodationService';

const Dashboard = () => {
    const [accommodations, setAccommodations] = useState([]);
    const [newAccommodation, setNewAccommodation] = useState({
        title: '',
        location: '',
        price: 0,
        guests: 0,
    });

    useEffect(() => {
        // Fetch accommodations on component mount
        const fetchData = async () => {
            const data = await getAccommodations();
            setAccommodations(data);
        };
        fetchData();
    }, []);

    const handleCreate = async (e) => {
        e.preventDefault();
        const createdAccommodation = await createAccommodation(newAccommodation);
        setAccommodations([...accommodations, createdAccommodation]);
    };

    const handleDelete = async (id) => {
        await deleteAccommodation(id);
        setAccommodations(accommodations.filter((acc) => acc._id !== id));
    };

    return (
        <div>
            <h2>Dashboard</h2>
            <h3>Create New Accommodation</h3>
            <form onSubmit={handleCreate}>
                <input
                    type="text"
                    placeholder="Title"
                    value={newAccommodation.title}
                    onChange={(e) => setNewAccommodation({ ...newAccommodation, title: e.target.value })}
                />
                <input
                    type="text"
                    placeholder="Location"
                    value={newAccommodation.location}
                    onChange={(e) => setNewAccommodation({ ...newAccommodation, location: e.target.value })}
                />
                <input
                    type="number"
                    placeholder="Price"
                    value={newAccommodation.price}
                    onChange={(e) => setNewAccommodation({ ...newAccommodation, price: Number(e.target.value) })}
                />
                <input
                    type="number"
                    placeholder="Guests"
                    value={newAccommodation.guests}
                    onChange={(e) => setNewAccommodation({ ...newAccommodation, guests: Number(e.target.value) })}
                />
                <button type="submit">Create</button>
            </form>

            <h3>Existing Accommodations</h3>
            <ul>
                {accommodations.map((acc) => (
                    <li key={acc._id}>
                        {acc.title} - {acc.location} - ${acc.price}
                        <button onClick={() => handleDelete(acc._id)}>Delete</button>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default Dashboard;
