import React, { useEffect, useState } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import { FaSearch, FaGlobe, FaUserCircle, FaBars } from "react-icons/fa";
import "./FilteredListings.css";

function FilteredListings() {
  const navigate = useNavigate();
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [listings, setListings] = useState([]);
  const [locationName, setLocationName] = useState("");
  const search = useLocation().search;
  const locationQuery = new URLSearchParams(search).get("location");

  const toggleDropdown = () => {
    setDropdownOpen(!dropdownOpen);
    console.log("Dropdown state:", !dropdownOpen); // Debugging line
};

  useEffect(() => {
    const fetchListings = async () => {
      const endpoint = locationQuery
        ? `/api/listings?location=${locationQuery}`
        : "/api/listings";
      const res = await fetch(endpoint);
      const data = await res.json();
      setListings(data);
      setLocationName(locationQuery || "All locations");
    };

    fetchListings();
  }, [locationQuery]);

  return (
    <div className="filtered-listings">
      <header className="navbar">
      <div className="navbar-logo" onClick={() => navigate('/')}>
                <img
                    src="https://logos-world.net/wp-content/uploads/2020/10/Airbnb-Logo-2014.png"
                    alt="Airbnb Logo"
                />
            </div>
            <div className="navbar-center">
                <span>Places to stay</span>
                <span>Experiences</span>
                <span>Online Experiences</span>
            </div>
            <div className="navbar-end">
                <span className="navbar-host" onClick={() => navigate('/host-login')}>Become a Host</span>
                <FaGlobe className="icon globe-icon" />
                <div
                    className="profile-section"
                    onClick={toggleDropdown}
                >
                    <FaBars className="menu-icon" />
                    <FaUserCircle className="user-icon" />
                    {dropdownOpen && (
                        <div className="dropdown-menu">
                            {isLoggedIn ? (
                                <span onClick={handleLogout}>Logout</span>
                            ) : (
                                <span onClick={handleLoginNavigation}>Login</span>
                            )}
                        </div>
                    )}
                </div>
                {isLoggedIn && <span className="welcome-text">Welcome, {username}</span>}
            </div>
      </header>

      <div className="content">
        <h2>
          {listings.length} stays in {locationName}
        </h2>
        <div className="listings-grid">
          {listings.map((listing) => (
            <div className="listing-card" key={listing._id}>
              <img src={listing.image} alt={listing.title} />
              <h3>{listing.title}</h3>
              <p>{listing.location}</p>
              <p>${listing.price} / night</p>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default FilteredListings;
