import React, { useState, useEffect, useCallback } from 'react';
import { useNavigate } from 'react-router-dom';
import DashboardNavbar from '../components/DashboardNavbar';
import AccommodationListings from '../components/AccommodationListings';
import Reservations from '../components/Reservations';
import CreateListing from '../components/CreateListing';
import axios from 'axios';

const HostDashboard = () => {
    const [view, setView] = useState('listings'); // Default view is 'listings'
    const [listings, setListings] = useState([]);
    const [reservations, setReservations] = useState([]);
    const [successMessage, setSuccessMessage] = useState('');

    const navigate = useNavigate();
    const hostId = localStorage.getItem("userId"); // Host ID stored in localStorage
    const hostName = localStorage.getItem("username"); // Host name stored in localStorage

    // Using useCallback to avoid recreating functions on every render
    const fetchListings = useCallback(async () => {
        try {
            const response = await axios.get(`/api/listings/host/${hostId}`);
            setListings(response.data);
        } catch (error) {
            console.error("Error fetching listings:", error);
        }
    }, [hostId]);

    const fetchReservations = useCallback(async () => {
        try {
            const response = await axios.get(`/api/reservations/host/${hostId}`);
            setReservations(response.data);
        } catch (error) {
            console.error("Error fetching reservations:", error);
        }
    }, [hostId]);

    useEffect(() => {
        if (view === 'listings') {
            fetchListings();
        } else if (view === 'reservations') {
            fetchReservations();
        }
    }, [view, fetchListings, fetchReservations]);

    const handleCreateListingSuccess = (message) => {
        setSuccessMessage(message);
        fetchListings(); // Refresh listings
    };

    // Logout function
    const handleLogout = () => {
        localStorage.removeItem("token"); // Clear token
        localStorage.removeItem("userId"); // Clear user ID
        localStorage.removeItem("username"); // Clear username
        navigate('/home'); // Redirect to home page
    };

    return (
        <div>
            {/* Navbar with Airbnb logo, welcome message, and logout */}
            <nav className="navbar">
                <h2 className="navbar-logo">airbnb</h2>
                <div className="navbar-user-info">
                    <span>Welcome, {hostName}</span>
                    <button onClick={handleLogout} className="logout-button">Logout</button>
                </div>
            </nav>
            
            <DashboardNavbar setView={setView} />
            
            {successMessage && <p className="success-message">{successMessage}</p>}
            
            {view === 'listings' && <AccommodationListings listings={listings} />}
            {view === 'reservations' && <Reservations reservations={reservations} />}
            {view === 'create' && <CreateListing onSuccess={handleCreateListingSuccess} />}
        </div>
    );

    
};

export default HostDashboard;
