// frontend/src/components/CreateListing.js

import React, { useState } from 'react';
import axios from 'axios';

const CreateListing = ({ onSuccess }) => {
    const [title, setTitle] = useState('');
    const [description, setDescription] = useState('');
    const [price, setPrice] = useState('');
    const [capacity, setCapacity] = useState('');
    const [imageUrl, setImageUrl] = useState('');

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post('/api/listings', {
                title,
                description,
                price,
                capacity,
                imageUrl,
                hostId: localStorage.getItem("userId"), // Associate the listing with the logged-in host
            });
            if (response.status === 201) {
                onSuccess("Listing created successfully!");
                setTitle('');
                setDescription('');
                setPrice('');
                setCapacity('');
                setImageUrl('');
            }
        } catch (error) {
            console.error("Error creating listing:", error);
        }
    };

    return (
        <form onSubmit={handleSubmit}>
            <input type="text" value={title} onChange={(e) => setTitle(e.target.value)} placeholder="Title" required />
            <input type="text" value={description} onChange={(e) => setDescription(e.target.value)} placeholder="Description" required />
            <input type="number" value={price} onChange={(e) => setPrice(e.target.value)} placeholder="Price per night" required />
            <input type="number" value={capacity} onChange={(e) => setCapacity(e.target.value)} placeholder="Capacity" required />
            <input type="text" value={imageUrl} onChange={(e) => setImageUrl(e.target.value)} placeholder="Image URL" />
            <button type="submit">Create Listing</button>
        </form>
    );
};

export default CreateListing;
