import axiosInstance from './axiosInstance';

export const getAccommodations = async () => {
    const { data } = await axiosInstance.get('/accommodations');
    return data;
};

export const createAccommodation = async (accommodation) => {
    const { data } = await axiosInstance.post('/accommodations', accommodation);
    return data;
};

export const updateAccommodation = async (id, updatedData) => {
    const { data } = await axiosInstance.put(`/accommodations/${id}`, updatedData);
    return data;
};

export const deleteAccommodation = async (id) => {
    await axiosInstance.delete(`/accommodations/${id}`);
};
