import React, { useEffect, useState } from 'react';
import { getAccommodations, createAccommodation, deleteAccommodation, u } from '../services/accommodationService';

const Dashboard = () => {
    const [accommodations, setAccommodations] = useState([]);
    const [newAccommodation, setNewAccommodation] = useState({
        title: '',
        location: '',
        description: '',
        price: 0,
        guests: 1,
        bedrooms: 1,
        bathrooms: 1,
        amenities: [],
        images: []
    });

    useEffect(() => {
        // Fetch accommodations when the component mounts
        const fetchData = async () => {
            const data = await getAccommodations();
            setAccommodations(data);
        };
        fetchData();
    }, []);

    const handleCreate = async (e) => {
        e.preventDefault();

        try {
            const createdAccommodation = await createAccommodation(newAccommodation);
            setAccommodations([...accommodations, createdAccommodation]);
            setNewAccommodation({
                title: '',
                location: '',
                description: '',
                price: 0,
                guests: 1,
                bedrooms: 1,
                bathrooms: 1,
                amenities: [],
                images: []
            });
        } catch (error) {
            console.error("Error creating accommodation:", error);
            alert("Failed to create accommodation");
        }
    };

    const handleDelete = async (id) => {
        try {
            await deleteAccommodation(id);
            
            // Update the accommodations list by filtering out the deleted item
            setAccommodations(accommodations.filter((acc) => acc._id !== id));
        } catch (error) {
            console.error("Error deleting accommodation:", error);
            alert("Failed to delete accommodation");
        }
    };

    return (
        <div>
            <h2>Dashboard</h2>
            <h3>Create New Accommodation</h3>
            <form onSubmit={handleCreate}>
                <input
                    type="text"
                    placeholder="Title"
                    value={newAccommodation.title}
                    onChange={(e) => setNewAccommodation({ ...newAccommodation, title: e.target.value })}
                />
                <input
                    type="text"
                    placeholder="Location"
                    value={newAccommodation.location}
                    onChange={(e) => setNewAccommodation({ ...newAccommodation, location: e.target.value })}
                />
                <input
                    type="text"
                    placeholder="Description"
                    value={newAccommodation.description}
                    onChange={(e) => setNewAccommodation({ ...newAccommodation, description: e.target.value })}
                />
                <input
                    type="number"
                    placeholder="Price"
                    value={newAccommodation.price}
                    onChange={(e) => setNewAccommodation({ ...newAccommodation, price: Number(e.target.value) })}
                />
                <input
                    type="number"
                    placeholder="Guests"
                    value={newAccommodation.guests}
                    onChange={(e) => setNewAccommodation({ ...newAccommodation, guests: Number(e.target.value) })}
                />
                <input
                    type="number"
                    placeholder="Bedrooms"
                    value={newAccommodation.bedrooms}
                    onChange={(e) => setNewAccommodation({ ...newAccommodation, bedrooms: Number(e.target.value) })}
                />
                <input
                    type="number"
                    placeholder="Bathrooms"
                    value={newAccommodation.bathrooms}
                    onChange={(e) => setNewAccommodation({ ...newAccommodation, bathrooms: Number(e.target.value) })}
                />
                <button type="submit">Create</button>
            </form>

            <h3>Existing Accommodations</h3>
            <ul>
                {accommodations.map((acc) => (
                    <li key={acc._id}>
                        {acc.title} - {acc.location} - ${acc.price}
                        <button onClick={() => handleDelete(acc._id)}>Delete</button>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default Dashboard;
