import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams, useNavigate } from 'react-router-dom';
import ''

const UpdateListing = () => {
  const { id } = useParams(); // Get listing ID from URL
  const navigate = useNavigate(); // For navigation after update

  const [form, setForm] = useState({
    title: '',
    location: '',
    description: '',
    rooms: '',
    guests: '',
    baths: '',
    price: '',
    amenities: [],
    rating: '',
    pricePerNight: '',
    image: '',
  });

  // Fetch the listing data when the component loads
  useEffect(() => {
    const fetchListing = async () => {
      try {
        const token = localStorage.getItem('token'); // Get auth token
        const response = await axios.get(`http://localhost:5000/api/accommodations/${id}`, {
          headers: { Authorization: `Bearer ${token}` },
        });
        const listing = response.data;

        // Populate the form with the fetched data
        setForm({
          title: listing.title,
          location: listing.location,
          description: listing.description,
          rooms: listing.rooms,
          guests: listing.guests,
          baths: listing.baths,
          price: listing.price,
          amenities: listing.amenities.join(', '), // Convert array to a comma-separated string
          rating: listing.rating || '',
          pricePerNight: listing.pricePerNight || '',
          image: listing.image || '',
        });
      } catch (error) {
        console.error('Error fetching listing:', error.response?.data || error.message);
      }
    };
    fetchListing();
  }, [id]);

  // Handle form input changes
  const handleChange = (e) => {
    const { name, value } = e.target;
    setForm({ ...form, [name]: value });
  };

  // Handle form submission to update the listing
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem('token'); // Get auth token

      // Convert amenities back to an array
      const updatedForm = {
        ...form,
        amenities: form.amenities.split(',').map((a) => a.trim()),
      };

      const response = await axios.put(
        `http://localhost:5000/api/accommodations/${id}`,
        updatedForm,
        { headers: { Authorization: `Bearer ${token}` } }
      );

      // Show success alert and navigate back to the listings page
      alert('Listing updated successfully!');
      navigate('/view-listings', { state: { updatedListing: response.data } });
    } catch (error) {
      console.error('Error updating listing:', error.response?.data || error.message);
      alert('Failed to update listing.');
    }
  };

  return (
    <form className="listing-form" onSubmit={handleSubmit}>
      <h2>Edit Listing</h2>
      <input
        type="text"
        name="title"
        value={form.title}
        onChange={handleChange}
        placeholder="Title"
        required
      />
      <input
        type="text"
        name="location"
        value={form.location}
        onChange={handleChange}
        placeholder="Location"
        required
      />
      <textarea
        name="description"
        value={form.description}
        onChange={handleChange}
        placeholder="Description"
        required
      />
      <input
        type="number"
        name="rooms"
        value={form.rooms}
        onChange={handleChange}
        placeholder="Number of Rooms"
      />
      <input
        type="number"
        name="guests"
        value={form.guests}
        onChange={handleChange}
        placeholder="Number of Guests"
      />
      <input
        type="number"
        name="baths"
        value={form.baths}
        onChange={handleChange}
        placeholder="Number of Baths"
      />
      <input
        type="number"
        name="price"
        value={form.price}
        onChange={handleChange}
        placeholder="Price"
      />
      <textarea
        name="amenities"
        value={form.amenities}
        onChange={handleChange}
        placeholder="Amenities (comma-separated)"
      />
      <input
        type="number"
        name="rating"
        value={form.rating}
        onChange={handleChange}
        placeholder="Rating"
      />
      <input
        type="number"
        name="pricePerNight"
        value={form.pricePerNight}
        onChange={handleChange}
        placeholder="Price Per Night"
      />
      <input
        type="text"
        name="image"
        value={form.image}
        onChange={handleChange}
        placeholder="Image URL"
      />
      <button type="submit">Save Changes</button>
    </form>
  );
};

export default UpdateListing;
