import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams, useNavigate } from 'react-router-dom';

const UpdateListing = () => {
    const { id } = useParams();
    const navigate = useNavigate();
    const [form, setForm] = useState({
      title: '',
      location: '',
      description: '',
      rooms: '',
      beds: '',
      guests: '',
      baths: '',
      price: '',
      amenities: [],
    });
  
    useEffect(() => {
      const fetchListing = async () => {
        try {
          const token = localStorage.getItem('token');
          const response = await axios.get(`http://localhost:5000/api/accommodations/${id}`, {
            headers: { Authorization: `Bearer ${token}` },
          });
          const listing = response.data;
          setForm({
            title: listing.title,
            location: listing.location,
            description: listing.description,
            rooms: listing.rooms,
            beds: listing.beds,
            guests: listing.guests,
            baths: listing.baths,
            price: listing.price,
            amenities: listing.amenities || [],
          });
        } catch (error) {
          console.error('Error fetching listing:', error.message);
        }
      };
      fetchListing();
    }, [id]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem('token');
      const response = await axios.put(
        `http://localhost:5000/api/accommodations/${id}`,
        {
          ...formData,
          amenities: formData.amenities.split(',').map((a) => a.trim()), // Convert back to an array
        },
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );
      const updatedListing = response.data; // Get updated listing
      navigate('/view-listings', { state: { updatedListing } }); // Pass updated listing to View Listings
    } catch (error) {
      console.error('Error updating listing:', error.response?.data || error.message);
    }
  };
  

