import React, { useState } from 'react';
import HostNavbar from './HostNavbar'; // Make sure to import the new navbar
import './HostDashboard.css'; // Create a CSS file for custom styles

function HostDashboard() {
    const [activeTab, setActiveTab] = useState('listings');
    const username = localStorage.getItem('username');

    const renderContent = () => {
        switch (activeTab) {
            case 'reservations':
                return <div>View Reservations Content</div>;
            case 'listings':
                return (
                    <div className="listing-view">
                        <h3>My Hotel List</h3>
                        <div className="listing-item">
                            <img src="https://example.com/hotel.jpg" alt="Hotel" />
                            <div>
                                <h4>Sandton City Hotel</h4>
                                <p>4-6 guests • Entire Home • 5 beds • 3 bath<br />
                                Wifi • Kitchen • Free Parking</p>
                                <p>5.0 ⭐ (318 reviews)</p>
                                <div className="price">$325 <span>/night</span></div>
                            </div>
                            <button className="update-btn">Update</button>
                            <button className="delete-btn">Delete</button>
                        </div>
                    </div>
                );
            case 'createListing':
                return <div>Create Listing Content</div>;
            default:
                return null;
        }
    };

    return (
        <div>
            <HostNavbar username={username} />
            <div className="dashboard-tabs">
                <button onClick={() => setActiveTab('reservations')}>View Reservations</button>
                <button onClick={() => setActiveTab('listings')}>View Listings</button>
                <button onClick={() => setActiveTab('createListing')}>Create Listing</button>
            </div>
            <div className="dashboard-content">
                {renderContent()}
            </div>
        </div>
    );
}

export default HostDashboard;
