import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import "./ListingsPage.css";

function ListingsPage() {
  const { location } = useParams(); // Retrieve location from the route
  const [listings, setListings] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchListings = async () => {
        try {
            const locationParam = location === 'All locations' ? 'all' : location;
            const response = await fetch(`http://localhost:5000/api/listings/${locationParam}`);
            if (!response.ok) {
                throw new Error(`HTTP error! Status: ${response.status}`);
            }
            const data = await response.json();
            setListings(data);
        } catch (error) {
            console.error('Error fetching listings:', error.message);
        }
    };
    fetchListings();
}, [location]);

  if (loading) {
    return <p>Loading listings...</p>;
  }

  return (
    <div className="listings-page">
      <h2>{listings.length} stays in {location === "all" ? "all locations" : location}</h2>
      <div className="listings-grid">
        {listings.map((listing) => (
          <div className="listing-card" key={listing.id}>
            <img
              src={listing.mainImage}
              alt={listing.title}
              className="listing-image"
              onError={(e) => (e.target.src = "/images/placeholder.jpg")}
            />
            <div className="listing-info">
              <h3>{listing.title}</h3>
              <p>
                {listing.guests} guests · {listing.rooms} rooms · {listing.baths} baths
              </p>
              <div className="amenities">
                {listing.amenities.map((amenity, index) => (
                  <span key={index}>{amenity}</span>
                ))}
              </div>
              <div className="price-rating">
                <span className="price">${listing.price} / night</span>
                <span className="rating">{listing.rating}★</span>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default ListingsPage;
