import React, { useState } from 'react';
import axiosInstance from '../services/axiosInstance';
import { useNavigate } from 'react-router-dom';

const Login = ({ userType }) => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');
    const navigate = useNavigate();

    const handleLogin = async (e) => {
        e.preventDefault();
        try {
            const { data } = await axiosInstance.post('/users/login', { username, password });
            localStorage.setItem('token', data.token);      // Save the token
            localStorage.setItem('username', data.username); // Save the username
            localStorage.setItem('role', data.role);         // Save the user role

            // Redirect based on role
            if (data.role === 'host') {
                navigate('/dashboard');                      // Host goes to dashboard
            } else {
                navigate('/home');                           // User goes to home page
            }
        } catch (err) {
            setError('Invalid credentials');
        }
    };

    return (
        <div>
            <h2>{userType === 'host' ? 'Host Login' : 'User Login'}</h2>
            <form onSubmit={handleLogin}>
                <input
                    type="text"
                    placeholder="Username"
                    value={username}
                    onChange={(e) => setUsername(e.target.value)}
                />
                <input
                    type="password"
                    placeholder="Password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                />
                <button type="submit">Login</button>
            </form>
            {error && <p style={{ color: 'red' }}>{error}</p>}
        </div>
    );
};

export default Login;
