const MyReservationsPage = () => {
  const [reservations, setReservations] = useState([]);

  useEffect(() => {
    const fetchReservations = async () => {
      try {
        const response = await axios.get("http://localhost:5000/api/reservations/user", {
          headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
        });
        setReservations(response.data);
      } catch (error) {
        console.error("Error fetching reservations:", error.message);
      }
    };

    fetchReservations();
  }, []);

  const handleDelete = async (reservationId) => {
    try {
      await axios.delete(`http://localhost:5000/api/reservations/${reservationId}`, {
        headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
      });
      setReservations(reservations.filter((res) => res._id !== reservationId));
    } catch (error) {
      console.error("Error deleting reservation:", error.message);
    }
  };

  const formatDate = (date) => {
    // Create a new Date object
    const newDate = new Date(date);
    return newDate.toLocaleDateString(); // Format the date to a readable format (e.g., "MM/DD/YYYY")
  };

  return (
    <div>
      <h1>My Reservations</h1>
      <table>
        <thead>
          <tr>
            <th>Property</th>
            <th>Check-in</th>
            <th>Check-out</th>
            <th>Guests</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {reservations.map((res) => (
            <tr key={res._id}>
              <td>{`${res.property.title}, ${res.property.location}`}</td>
              <td>{formatDate(res.checkIn)}</td> {/* Use formatDate function to display the date properly */}
              <td>{formatDate(res.checkOut)}</td>
              <td>{res.guests}</td>
              <td>
                <button onClick={() => handleDelete(res._id)} className="delete-button">
                  Delete
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};
