import React, { useEffect, useState } from 'react';
import ListingForm from '../components/ListingForm';
import axios from 'axios';
import { useParams } from 'react-router-dom';

const UpdateListingPage = () => {
    const { id } = useParams();
    const [initialData, setInitialData] = useState(null);

    useEffect(() => {
        const fetchListing = async () => {
            try {
                const response = await axios.get(`/api/accommodations/${id}`);
                setInitialData(response.data);
            } catch (error) {
                console.error(error);
            }
        };

        fetchListing();
    }, [id]);

    const handleSubmit = async (data) => {
        try {
            const response = await axios.put(`/api/accommodations/${id}`, data);
            console.log(response.data);
            // Redirect to dashboard or display success message
        } catch (error) {
            console.error(error);
            // Display error message
        }
    };

    return (
        <div>
            <h1>Update Listing</h1>
            {initialData && <ListingForm onSubmit={handleSubmit} initialData={initialData} />}
        </div>
    );
};

export default UpdateListingPage;
