import React, { useEffect, useState } from "react";
import axios from "axios";
import "../components/ViewListings.css";

const ViewListings = () => {
  const [listings, setListings] = useState([]);
  const [isEditing, setIsEditing] = useState(null);
  const [editForm, setEditForm] = useState({
    title: "",
    location: "",
    description: "",
    rooms: "",
    baths: "",
    amenities: [],
    price: "",
  });

  // Fetch listings from the API
  useEffect(() => {
    const fetchListings = async () => {
      try {
        const token = localStorage.getItem("token");
        const response = await axios.get("http://localhost:5000/api/accommodations", {
          headers: { Authorization: `Bearer ${token}` },
        });
        setListings(response.data);
      } catch (error) {
        console.error("Failed to fetch listings:", error.response?.data || error.message);
      }
    };

    fetchListings();
  }, []);

  // Handle Edit button click
  const handleEditClick = (listing) => {
    setIsEditing(listing._id);
    setEditForm({
      title: listing.title,
      location: listing.location,
      description: listing.description,
      rooms: listing.rooms,
      baths: listing.baths,
      amenities: listing.amenities,
      price: listing.price,
    });
  };

  // Handle form input changes
  const handleEditChange = (e) => {
    const { name, value } = e.target;
    setEditForm((prev) => ({ ...prev, [name]: value }));
  };

  // Add new amenity input
  const handleAddAmenity = () => {
    setEditForm((prev) => ({
      ...prev,
      amenities: [...prev.amenities, ""],
    }));
  };

  // Update amenity value
  const handleAmenityChange = (index, value) => {
    const updatedAmenities = [...editForm.amenities];
    updatedAmenities[index] = value;
    setEditForm((prev) => ({ ...prev, amenities: updatedAmenities }));
  };

  // Handle Update Listing
  const handleUpdate = async (listingId) => {
    try {
      const response = await axios.put(`/api/accommodations/${listingId}`, editForm);
      const updatedListing = response.data;

      setListings((prevListings) =>
        prevListings.map((listing) =>
          listing._id === listingId ? updatedListing : listing
        )
      );

      setIsEditing(null);
      setEditForm({
        title: "",
        location: "",
        description: "",
        rooms: "",
        baths: "",
        amenities: [],
        price: "",
      });
    } catch (error) {
      console.error("Failed to update listing:", error);
    }
  };

  // Handle Delete Listing
  const handleDelete = async (listingId) => {
    try {
      await axios.delete(`/api/accommodations/${listingId}`);
      setListings((prevListings) => prevListings.filter((listing) => listing._id !== listingId));
    } catch (error) {
      console.error("Failed to delete listing:", error);
    }
  };

  return (
    <div className="view-listings-container">
      <h2>Accommodation Listings</h2>
      {listings.length > 0 ? (
        <div className="listings-grid">
          {listings.map((listing) => (
            <div key={listing._id} className="listing-card">
              <img src={`http://localhost:5000/${listing.mainImage}`} alt={listing.title} className="listing-image" />
              <div className="listing-details">
                <h3 className="listing-title">{listing.title}</h3>
                <p className="listing-location">{listing.location}</p>
                <p className="listing-description">{listing.description}</p>
                <p className="listing-info">
                  {listing.rooms} Rooms â€¢ {listing.baths} Baths
                </p>
                <p className="listing-amenities">
                  {listing.amenities.map((amenity, index) => (
                    <span key={index} className="amenity">{amenity}</span>
                  ))}
                </p>
                <p className="listing-price">${listing.price}/night</p>
                <div className="listing-actions">
                  {isEditing === listing._id ? (
                    <div className="edit-form">
                      <input
                        type="text"
                        name="title"
                        value={editForm.title}
                        onChange={handleEditChange}
                        placeholder="Title"
                      />
                      <input
                        type="text"
                        name="location"
                        value={editForm.location}
                        onChange={handleEditChange}
                        placeholder="Location"
                      />
                      <textarea
                        name="description"
                        value={editForm.description}
                        onChange={handleEditChange}
                        placeholder="Description"
                      />
                      <input
                        type="number"
                        name="rooms"
                        value={editForm.rooms}
                        onChange={handleEditChange}
                        placeholder="Rooms"
                      />
                      <input
                        type="number"
                        name="baths"
                        value={editForm.baths}
                        onChange={handleEditChange}
                        placeholder="Baths"
                      />
                      <input
                        type="number"
                        name="price"
                        value={editForm.price}
                        onChange={handleEditChange}
                        placeholder="Price per Night"
                      />
                      <div className="amenities-input">
                        <label>Amenities</label>
                        {editForm.amenities.map((amenity, index) => (
                          <input
                            key={index}
                            type="text"
                            value={amenity}
                            onChange={(e) => handleAmenityChange(index, e.target.value)}
                            placeholder={`Amenity ${index + 1}`}
                          />
                        ))}
                        <button type="button" onClick={handleAddAmenity}>
                          Add Amenity
                        </button>
                      </div>
                      <button onClick={() => handleUpdate(listing._id)} className="save-btn">
                        Save
                      </button>
                      <button onClick={() => setIsEditing(null)} className="cancel-btn">
                        Cancel
                      </button>
                    </div>
                  ) : (
                    <>
                      <button onClick={() => handleEditClick(listing)} className="edit-btn">
                        Edit
                      </button>
                      <button onClick={() => handleDelete(listing._id)} className="delete-btn">
                        Delete
                      </button>
                    </>
                  )}
                </div>
              </div>
            </div>
          ))}
        </div>
      ) : (
        <p>No listings available.</p>
      )}
    </div>
  );
};

export default ViewListings;
