import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import '../components/ListingDetailsPage.css';
import { FaStar, FaShareAlt, FaHeart, FaHome, FaCalendarAlt, FaDoorOpen, FaBroom, FaGlobe, FaUserCircle, FaBars} from 'react-icons/fa';

function ListingDetailsPage() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [username, setUsername] = useState("");
  const [profileDropdownOpen, setProfileDropdownOpen] = useState(false);
  const navigate = useNavigate();
  const { id } = useParams(); // Extract listing ID from URL
  const [listing, setListing] = useState(null);
  const [reservationDetails, setReservationDetails] = useState({
    checkIn: '',
    checkOut: '',
    guests: 1,
  });

  const [totalCost, setTotalCost] = useState(0);

  useEffect(() => {
    const fetchListingDetails = async () => {
      try {
        const response = await fetch(`http://localhost:5000/api/listing/${id}`);
        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }
        const data = await response.json();
        setListing(data);
      } catch (error) {
        console.error('Error fetching listing details:', error.message);
      }
    };

    fetchListingDetails();
  }, [id]);

  // Check for logged-in user
  useEffect(() => {
    const storedUsername = localStorage.getItem("username");
    if (storedUsername) {
      setIsLoggedIn(true);
      setUsername(storedUsername);
    }
  }, []);

  const handleLogout = () => {
    localStorage.removeItem("username");
    localStorage.removeItem("role");
    setIsLoggedIn(false);
    setUsername("");
    navigate("/");
  };

  const toggleProfileDropdown = () => {
    setProfileDropdownOpen((prev) => !prev);
  };

  useEffect(() => {
    // Calculate total cost
    const calculateTotalCost = () => {
      if (reservationDetails.checkIn && reservationDetails.checkOut) {
        const checkInDate = new Date(reservationDetails.checkIn);
        const checkOutDate = new Date(reservationDetails.checkOut);
        const days = Math.max(
          Math.floor((checkOutDate - checkInDate) / (1000 * 60 * 60 * 24)),
          0
        );
  
        // Base subtotal for the listing
        let subtotal = days * listing?.price || 0;
  
        // Check if the number of guests exceeds 2
        if (reservationDetails.guests > 2) {
          const extraGuests = reservationDetails.guests - 2;
          // Add extra charges for the additional guests (price per night * extra guests)
          subtotal += extraGuests * listing?.price * days;
        }
  
        // Add fixed fees
        const total = subtotal + 50 + 50 + 30; // Cleaning fee, service fee, occupancy taxes
        setTotalCost(total);
      }
    };
  
    calculateTotalCost();
  }, [reservationDetails, listing]);
  

  const handleReservation = () => {
    // Send reservation data to the backend
    alert('Reservation successfully created!');
  };

  if (!listing) {
    return <p>Loading...</p>;
  }

  const daysBetween = () => {
    if (reservationDetails.checkIn && reservationDetails.checkOut) {
      const checkInDate = new Date(reservationDetails.checkIn);
      const checkOutDate = new Date(reservationDetails.checkOut);
      return Math.max(
        Math.floor((checkOutDate - checkInDate) / (1000 * 60 * 60 * 24)),
        0
      );
    }
    return 0;
  };

  const clearDates = () => {
    setReservationDetails({
      ...reservationDetails,
      checkIn: '',
      checkOut: '',
    });
  };

  const reviews = [
    {
      profilePicture: 'path/to/image1.jpg',
      name: 'John Doe',
      date: 'Nov 2023',
      message: 'Great place to stay! Very clean and cozy.',
    },
    {
      profilePicture: 'https://img.freepik.com/free-photo/smiley-african-woman-with-golden-earrings_23-2148747979.jpg?ga=GA1.1.1086424708.1732098994&semt=ais_hybrid',
      name: 'Jane Smith',
      date: 'Oct 2023',
      message: 'Amazing location and wonderful host!',
    },
    {
      profilePicture: 'path/to/image3.jpg',
      name: 'Alice Brown',
      date: 'Sept 2023',
      message: 'Loved everything about it. Highly recommended!',
    },
    {
      profilePicture: 'path/to/image4.jpg',
      name: 'Mark Wilson',
      date: 'Aug 2023',
      message: 'Fantastic place! Will definitely come back.',
    },
    {
      profilePicture: 'path/to/image5.jpg',
      name: 'Emily Davis',
      date: 'July 2023',
      message: 'Such a peaceful and lovely home!',
    },
    {
      profilePicture: 'path/to/image6.jpg',
      name: 'Chris Green',
      date: 'June 2023',
      message: 'Perfect for a weekend getaway. Excellent!',
    },
  ];
  
  

  return (
    <div className="listing-details-page">
      {/* Navbar */}
      <header className="navbar">
        <div className="navbar-logo" onClick={() => navigate("/")}>
          <img
            src="https://logos-world.net/wp-content/uploads/2020/10/Airbnb-Logo-2014.png"
            alt="Airbnb Logo"
          />
        </div>
        <div className="navbar-center">
          <span>Places to stay</span>
          <span>Experiences</span>
          <span>Online Experiences</span>
        </div>
        <div className="navbar-end">
          <span className="navbar-host" onClick={() => navigate("/host-login")}>
            Become a Host
          </span>
          <FaGlobe className="icon globe-icon" />
          <div className="profile-section" onClick={toggleProfileDropdown}>
            <FaBars className="menu-icon" />
            <FaUserCircle className="user-icon" />
            {profileDropdownOpen && (
              <div className="profile-dropdown-menu">
                {isLoggedIn ? (
                  <span onClick={handleLogout}>Logout</span>
                ) : (
                  <span onClick={() => navigate("/user-login")}>Login</span>
                )}
              </div>
            )}
          </div>
          {isLoggedIn && (
            <span className="welcome-text">Welcome, {username}</span>
          )}
        </div>
      </header>

      {/* Listing Details Section */}
      <section className="details-section">
        {/* Title and Share/Save */}
        <div className="listing-title">
          <h1>
            {listing.title} in {listing.location}
          </h1>
          <div className="listing-subtitle">
            <span>
              <FaStar className="star-icon" /> 5.0 (50 reviews) - {listing.location}
            </span>
            <div className="listing-actions">
              <span>
                <FaShareAlt /> Share
              </span>
              <span>
                <FaHeart /> Save
              </span>
            </div>
          </div>
        </div>

        {/* Images Section */}
        <div className="image-gallery">
          <img
            src={`http://localhost:5000/${listing.mainImage}`}
            alt="Main"
            className="main-image"
          />
          <div className="other-images">
            {listing.images.map((image, index) => (
              <img
                key={index}
                src={`http://localhost:5000/${image}`}
                alt={`Gallery ${index + 1}`}
                className="gallery-image"
              />
            ))}
          </div>
        </div>

        {/* Listing Information and Booking */}
        <div className="info-booking-section">
          <div className="left-info">
            <h2>
              Entire {listing.title} hosted by Jamz
            </h2>
            <p>
              {listing.guests} Guests - Entire {listing.title} - {listing.rooms} Rooms - {listing.baths} Baths
            </p>
            <div className="host-profile">
              <img
                src="https://img.freepik.com/premium-psd/hiphop-rapper-person-3d_716128-1290.jpg"
                alt="Host"
                className="host-profile-image"
              />
            </div>

            <div className="features">
              <div>
                <FaHome /> <strong>Entire Apartment</strong>
                <p>You'll have the apartment for yourself.</p>
              </div>
              <div>
                <FaBroom /> <strong>Enhanced Cleaning</strong>
                <p>This Host committed to Airbnb's 5-step enhanced cleaning process.</p>
              </div>
              <div>
                <FaDoorOpen /> <strong>Self Check-in</strong>
                <p>Check yourself in with the keypad.</p>
              </div>
              <div>
                <FaCalendarAlt /> <strong>Free cancellation before Dec 25</strong>
              </div>
            </div>

            <hr />
            <p className="listing-description">{listing.description}</p>
          </div>

          {/* Booking Card */}
          <div className="reservation-card">
            <h2>
              ${listing.price} / night <span>⭐ 5.0 (50)</span>
            </h2>
            <div className="booking-form">
              <div className="form-row">
                <label>Check-in</label>
                <input
                  type="date"
                  value={reservationDetails.checkIn}
                  onChange={(e) =>
                    setReservationDetails((prev) => ({
                      ...prev,
                      checkIn: e.target.value,
                    }))
                  }
                />
                <label>Check-out</label>
                <input
                  type="date"
                  value={reservationDetails.checkOut}
                  onChange={(e) =>
                    setReservationDetails((prev) => ({
                      ...prev,
                      checkOut: e.target.value,
                    }))
                  }
                />
              </div>
              <div className="form-row">
                <label>Guests</label>
                <input
                  type="number"
                  min="1"
                  value={reservationDetails.guests}
                  onChange={(e) =>
                    setReservationDetails((prev) => ({
                      ...prev,
                      guests: e.target.value,
                    }))
                  }
                />
              </div>
              <button onClick={handleReservation}>Reserve</button>
              <p>You won't be charged yet</p>

              {/* Pricing Details */}
              <div className="pricing-details">
                <div>
                  <span>
                    ${listing.price} x {daysBetween()} nights
                  </span>
                  <span>${daysBetween() * listing.price}</span>
                </div>
                <div>
                  <span>Cleaning fee</span>
                  <span>$50</span>
                </div>
                <div>
                  <span>Service fee</span>
                  <span>$50</span>
                </div>
                <div>
                  <span>Occupancy taxes and fees</span>
                  <span>$30</span>
                </div>
                <hr />
                <div>
                  <strong>Total</strong>
                  <strong>${totalCost}</strong>
                </div>
              </div>
            </div>
          </div>
        </div>
      </section>

      {/* Where You'll Sleep Section */}
  <section className="where-youll-sleep">
    <h2>Where You'll Sleep</h2>
    <img src={`http://localhost:5000/${listing.mainImage}`} alt="Spacious Bedroom" className="main-image" />
    <p className="image-caption">Spacious bedroom with comfortable bed</p>
    <p>Total Bedrooms: {listing?.rooms}</p>
    <hr />
  </section>

  {/* What This Place Offers Section */}
  <section className="what-this-place-offers">
    <h2>What this place offers</h2>
    <div className="amenities-list">
      {listing?.amenities?.map((amenity, index) => (
        <p key={index} className="amenity">{amenity}</p>
      ))}
    </div>
    <button className="view-amenities">View all 37 amenities</button>
    <hr />
  </section>

  {/* 7 Nights in Location Section */}
  <section className="seven-nights">
  <h2>7 Nights in {listing?.location}</h2>
  <div className="calendar-section">
    <div className="calendar">
      <h4>Check-in</h4>
      <input
        type="date"
        value={reservationDetails.checkIn}
        onChange={(e) =>
          setReservationDetails({ ...reservationDetails, checkIn: e.target.value })
        }
      />
    </div>
    <div className="calendar">
      <h4>Check-out</h4>
      <input
        type="date"
        value={reservationDetails.checkOut}
        onChange={(e) =>
          setReservationDetails({ ...reservationDetails, checkOut: e.target.value })
        }
      />
    </div>
  </div>
  <p className="calendar-text">
    Selected Dates: {reservationDetails.checkIn} - {reservationDetails.checkOut}
  </p>
  <button className="clear-dates" onClick={clearDates}>
    Clear dates
  </button>
  <hr />
</section>

  {/* Reviews and Ratings Section */}
  <section className="reviews-section">
  <p>⭐ 5.0 - 50 reviews</p>
  <div className="reviews">
    {reviews.slice(0, 6).map((review, index) => (
      <div className="review" key={index}>
        <img src={review.profilePicture} alt="Reviewer" className="reviewer-img" />
        <div>
          <p className="reviewer-name">{review.name}</p>
          <p className="review-date">{review.date}</p>
          <p className="review-message">{review.message}</p>
        </div>
      </div>
    ))}
  </div>
  <button className="show-reviews">Show all 50 reviews</button>
  <hr />
</section>
    </div>
  );
}

export default ListingDetailsPage;
